#include <bits/stdc++.h>

using namespace std;
const int maxn = 10001;
const int maxm = 50001;
const int maxh = 21;
const int inf = 1000000000;
int r[maxm], p[maxm], u[maxm], v[maxm], w[maxm];
struct edge {
    int l, t;
};
vector<edge> a[maxn];
int n, m;
int dep[maxn];
int s[maxn];      //stack<int> s;
int anc[maxn][maxh];
int lcs[maxn][maxh];
int wr[maxn];

bool cmp(const int i, const int j) { return w[i] > w[j]; }

int find(int x) {
    if (p[x] == x)return x;
    return p[x] = find(p[x]);
}

void dfs(int root) {
    int top = 0;
    dep[root] = 1;
    for (int i = 0; i < maxh; i++) {
        anc[root][i] = root;
        lcs[root][i] = inf;
    }
    s[++top] = root;
    while (top) {
        int x = s[top--];
        if (x != root)
            for (int i = 1; i < maxh; i++) {
                anc[x][i] = anc[anc[x][i - 1]][i - 1];
                lcs[x][i] = min(lcs[x][i - 1], lcs[anc[x][i - 1]][i - 1]);
            }
        for (int i = 0; i < a[x].size(); i++) {
            int y = a[x][i].t;
            if (y != anc[x][0]) {
                dep[y] = dep[x] + 1;
                anc[y][0] = x;
                lcs[y][0] = a[x][i].l;
                s[++top] = y;
            }
        }
        while (top && a[s[top]].size() == 0)top--;
    }
    return;
}

int swim(int &x, int h) {
    int tot = inf;
    for (int i = 0; h > 0; i++) {
        if (h & 1) {
            tot = min(tot, lcs[x][i]);
            x = anc[x][i];
        }
        h = (h >> 1);
    }
    return tot;
}

int lca(int x, int y) {
    int pos;
    if (dep[x] > dep[y])swap(x, y);
    int tot = swim(y, dep[y] - dep[x]);
    if (x == y)return tot;
    while (1) {
        for (pos = 0; anc[x][pos] != anc[y][pos]; pos++);
        if (pos == 0) {
            tot = min(lcs[y][0], tot);
            tot = min(lcs[x][0], tot);
            return tot;
        }
        tot = min(tot, lcs[x][pos - 1]);
        x = anc[x][pos - 1];
        tot = min(tot, lcs[y][pos - 1]);
        y = anc[y][pos - 1];
    }
    return -1;
}

int main() {
    ios::sync_with_stdio(false);
    cin >> n >> m;
    for (int i = 1; i <= m; i++) {
        int x, y, z;
        cin >> x >> y >> z;
        u[i] = x;
        v[i] = y;
        w[i] = z;
    }


