    for (int i = 1; i <= m; i++)r[i] = i;
    sort(r + 1, r + 1 + m, cmp);
    for (int i = 1; i <= m; i++) {
        int e = r[i];
        int x = find(v[e]);
        int y = find(u[e]);
        if (x != y) {
            p[x] = y;
            edge r;
            r.t = v[e];
            r.l = w[e];
            a[u[e]].push_back(r);
            r.t = u[e];
            a[v[e]].push_back(r);
        }
    }
    int q;
    cin >> q;
    for (int i = 1; i <= q; i++) {
        int x, y;
        cin >> x >> y;
        int ax = find(x);
        int ay = find(y);
        if (anc[ax][0] == 0)dfs(ax);
        if (anc[ay][0] == 0)dfs(ay);
        if (ax != ay)cout << -1 << endl;
        else cout << lca(x, y) << endl;
    }
    return 0;
}

